import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { motion } from 'framer-motion';
import Header from './components/Header';
import Home from './components/Home';
import Search from './components/Search';
import Recommendations from './components/Recommendations';
import About from './components/About';
import MusicPlayer from './components/MusicPlayer';

function App() {
  const [currentSong, setCurrentSong] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [playlist, setPlaylist] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);

  const handlePlaySong = (song) => {
    setCurrentSong(song);
    setIsPlaying(true);
  };

  const handlePlayPause = () => {
    setIsPlaying(!isPlaying);
  };

  const handleNext = () => {
    if (playlist.length > 0) {
      const nextIndex = (currentIndex + 1) % playlist.length;
      setCurrentIndex(nextIndex);
      setCurrentSong(playlist[nextIndex]);
    }
  };

  const handlePrevious = () => {
    if (playlist.length > 0) {
      const prevIndex = currentIndex === 0 ? playlist.length - 1 : currentIndex - 1;
      setCurrentIndex(prevIndex);
      setCurrentSong(playlist[prevIndex]);
    }
  };

  const handleVolumeChange = (volume) => {
    // Handle volume change
    console.log('Volume changed to:', volume);
  };

  return (
    <Router>
      <div className="min-h-screen animated-bg">
        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 0.5 }}
          className="min-h-screen pb-20 md:pb-0" // Add padding bottom for mobile
        >
          <Header />
          <main className="container mx-auto px-4 py-8">
            <Routes>
              <Route path="/" element={<Home onPlaySong={handlePlaySong} />} />
              <Route path="/search" element={<Search onPlaySong={handlePlaySong} />} />
              <Route path="/recommendations" element={<Recommendations onPlaySong={handlePlaySong} />} />
              <Route path="/about" element={<About />} />
            </Routes>
          </main>
          
          {/* Music Player */}
          <MusicPlayer
            currentSong={currentSong}
            isPlaying={isPlaying}
            onPlayPause={handlePlayPause}
            onNext={handleNext}
            onPrevious={handlePrevious}
            onVolumeChange={handleVolumeChange}
          />
        </motion.div>
      </div>
    </Router>
  );
}

export default App;
